import './App.css';
import HomePage from './pages/homepage/homepage.component'
import {Route, Switch} from 'react-router-dom';
import React from "react";
import Header from './components/header/header.component'
import SignInAndSignUp from './pages/sign-in-and-sign-up/sign-in-and-sign-up.component'
import ShopPage from './pages/shop/shop.component'

import {auth, createUserProfileDocument} from "./firebase/firebase.utils";

import {connect} from "react-redux";

import { setCurrentUser } from "./redux/user/user.actions";

class App extends React.Component {

    unsubscribeFromAuth = null;

    componentDidMount() {

        const {setCurrentUser} = this.props;

        this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
            // this.setState({currentUser: user})
            // createUserProfileDocument(user)
            if (userAuth) {
                const userRef = await createUserProfileDocument(userAuth);
                userRef.onSnapshot(snapshot => {
                    setCurrentUser({
                        id: snapshot.id,
                        ...snapshot.data()
                    })
                })
            } else {
                setCurrentUser({userAuth})
            }
        })
    }

    componentWillUnmount() {
        this.unsubscribeFromAuth()
    }

    render() {
        return (
            <div>
                <Header />
                <Switch>
                    <Route exact path='/' component={HomePage}/>
                    <Route exact path='/shop' component={ShopPage}/>
                    <Route exact path='/signin' component={SignInAndSignUp}/>
                </Switch>
            </div>
        );
    }
}

// dispatch is a ways of react to tell every reducer that there is an action.
//now we need to pass it an action.
//this is exactly what are user action is doing.
//it is getting a user and then return an action object.
//this setCurrentUser function is a function that was created by invoking the user action with user.
const mapDispatchToProps = dispatch => {
  setCurrentUser: user => dispatch(setCurrentUser(user))
};

export default connect(null, mapDispatchToProps)(App);
